import React from 'react';
//import { useHistory } from 'react-router-dom'; 

import '../styles/Header.css';

function Header() {
  // const history = useHistory();

  // const handleLoginClick = () => {
  //   history.push('/login'); 
  // };

  // const handleRegisterClick = () => {
  //   history.push('/register');     
  // };

  const handleClick = () => {
    window.location.href = '#';
  };
  function input_btn(){
    return(
      <div id="loginModal" className="modal">
            <div className="modal-content">
                <span className="close">&times;</span>
                <h2>Вход</h2>
                <form id="loginForm" method="post" action="login.php">
                    <label htmlFor="loginUsername">Имя пользователя:</label>
                    <input type="text" id="loginUsername" name="username"></input>
                    <label htmlFor="loginPassword">Пароль:</label>
                    <input type="password" id="loginPassword" name="password"></input>
                    <input type="submit" value="Войти"></input>
                </form>
                <p>Еще нет аккаунта? <a href="#">Зарегистрироваться?</a></p>
            </div>
        </div>
    );
  };
  return (
    <div className="header">
      <div className="title" onClick={handleClick}>Главная</div>
      <div className="logo">Логотип</div>
      <div className="auth-links">
        <button className="login" onlick={input_btn}>Войти</button>
        <div>или</div>
        <div className="register" >Зарегистрироваться</div>
      </div>
    </div>
  );
}

export default Header;
