import React from 'react';
import ReactDOM from 'react-dom/client';
import { render } from 'react-dom'; 

const user = undefined;

const vhod = () => {

  if (user) {

  }
  else {
    render(
      (
        <div id="loginModal" class="modal">
          <div className="modal-content">
            <span class="close" onclick="closeModal('loginModal')">&times;</span>
            <h2>Вход</h2>
            <form id="loginForm" method="post" action="login.php">
              <label for="loginUsername">Имя пользователя:</label>
              <input type="text" id="loginUsername" name="username"></input>
              <label for="loginPassword">Пароль:</label>
              <input type="password" id="loginPassword" name="password"></input>
              <input type="submit" value="Войти"></input>
            </form>
            <p>Еще нет аккаунта? <a href="#" onclick="switchModal('loginModal', 'registerModal')">Зарегистрироваться?</a></p>
          </div>
        </div>
      ),document.getElementById("ranom")
    )
    }
  }


class Button_enter extends React.Component {
  render() { return (<button onClick={vhod} id="button_enter">вход или пользователь</button>); }
}

const root = ReactDOM.createRoot(document.getElementById('button_enter'));
root.render(
  <Button_enter />
);
