import React from "react";
import ReactDOM from 'react-dom/client';


const [modalActive, setModalActive] = useState(true);

class Log_in extends React.Component {
    render() {
        return(
            <div id="loginModal" className="modal">
                <div className="modal-content">
                    <span className="close" onClick={() => setActive(false)}>&times;</span>
                    <h2>Вход</h2>
                    <form id="loginForm" method="post" action="login.php">
                        <label htmlFor="loginUsername">Имя пользователя:</label>
                        <input type="text" id="loginUsername" name="username"></input>
                        <label htmlFor="loginPassword">Пароль:</label>
                        <input type="password" id="loginPassword" name="password"></input>
                        <input type="submit" value="Войти"></input>
                    </form>
                    <p>Еще нет аккаунта? <a href="#" /*Onclick="switchModal('loginModal', 'registerModal')"*/>Зарегистрироваться?</a></p>
                </div>
            </div>
        );
    }
}

export default Log_in;
