  }
  else {
    vhod();
  }


class Button_enter extends React.Component {
  render() { return (<button onClick={vhod} id="button_enter">вход или пользователь</button>); }
}

const root = ReactDOM.createRoot(document.getElementById('button_enter'));
root.render(
  <Button_enter />
);
